mapper.update:
update comment
set bad=bad + 1
where id = #{id}

update comment
set good=good + 1
where id = #{id}

update blog
set click=click + 1
where id = #{id}

update link
set click=click + 1
where id = #{id}

mapper.select:
select b.id, b.category_id, b.title, b.summary,b.weight, b.header_img, b.status,b.support,b.like,b.click, b.comment, b.create_time,b.update_time,count(c.id) as comment_count from blog b left join comment c on b.id=c.page_id and c.delete_time is null where b.delete_time is null
AND b.category_id =#{categoryId}
AND b.support = #{support}
and date_format( b.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format( b.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
group by b.id

select t.id, color, title,type, create_by, create_time
from tag t
left join tag_mapping tm on t.id = tm.tag_id
where t.type=#{type}
and tm.blog_id=#{id}
and tm.note_id=#{id}
and tm.book_id=#{id}
